import { Component } from '@angular/core';

@Component({
  selector: 'app-mens',
  templateUrl: './mens.component.html',
  styleUrls: ['./mens.component.css']
})
export class MensComponent {
  slidePath1='assets/men/carousel1.png';
  slidePath2='assets/men/carousel2.png';
  slidePath3='assets/men/carousel3.png';



}
<app-navbar></app-navbar>
<app-carousel></app-carousel>
<div class="card">
  <img src="assets/men/Bomber.jpg" alt="Denim Jeans" style="width:100%">
  <h1>Stake Bomber Jacket</h1>
  <p class="price">price$19.99</p>
  <p>Some text about the jeans..</p>
  <p><button>Add to Cart</button></p>
</div>


<app-footer></app-footer>
