import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css']
})
export class CarouselComponent implements OnInit {
  images = [
    // { src: 'assets/men/Bomber.jpg', alt: 'Image 1' },
    // { src: 'assets/men/Dior.jpg', alt: 'Image 2' },
    // { src: 'assets/men/k1.jpg', alt: 'Image 3' },
    // { src: 'assets/men/k2.jpg', alt: 'Image 4' },
    // { src: 'assets/men/k4.jpg', alt: 'Image 5' },
    // { src: 'assets/men/k9.jpg', alt: 'Image 6' },
    // { src: 'assets/men/k8.jpg', alt: 'Image 7' },
    // { src: 'assets/men/k7.jpg', alt: 'Image 8' },
    // { src: 'assets/men/k5.jpg', alt: 'Image 9' },

  ];

  slideOffset = 0;
  currentIndex = 0;

  ngOnInit() {
    this.transitionToNextSlide();
  }

  transitionToNextSlide() {
    if (this.currentIndex < this.images.length - 1) {
      this.currentIndex++;
      this.slideOffset = -this.currentIndex * 100;
    } else {
      // Last slide or no more images, stop the carousel
      this.currentIndex;
    }

    if (this.images[this.currentIndex].src === '') {
      // Empty image, stop the carousel
      return;
    }

    setTimeout(() => {
      this.transitionToNextSlide();
    }, 8000);
  }


}
<div class="carousel">
  <div class="carousel-slide" [style.transform]="'translateX(' + slideOffset + '%)'">
    <img style="height: 300px; width: 500px; margin-top: 30px;" *ngFor="let image of images" [src]="image.src" [alt]="image.alt">
  </div>
</div>
