import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css']
})
export class CarouselComponent implements OnInit {
  images = [
    { src: 'assets/men/Bomber.jpg', alt: 'Image 1' },
    { src: 'assets/men/Dior.jpg', alt: 'Image 2' },
    { src: 'assets/men/carousel2.png', alt: 'Image 3' },
    { src: 'assets/men/carousel3.png', alt: 'Image 4' },
    { src: 'assets/men/carousel4.jpg', alt: 'Image 5' },
    { src: 'assets/men/Bomber.jpg', alt: 'Image 6' },
  ];

  slideOffset = 0;
  currentIndex = 0;

  ngOnInit() {
    setInterval(() => {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
      this.slideOffset = -this.currentIndex * 100;
    }, 3000);
  }

}
<div class="carousel">
  <div class="carousel-slide" [style.transform]="'translateX(' + slideOffset + '%)'">
    <img *ngFor="let image of images" [src]="image.src" [alt]="image.alt">
  </div>
</div>

<p>carousel works!</p>
